mapper.insert:
INSERT INTO HBT_BOARD (
    HBT_BOARD_NUM
    , HBT_BOARD_TITLE
    , HBT_BOARD_CONTENT
    , MEM_CODE
    
    , IS_PRIVATE
    , PRIVATE_PW
    
    
    , IS_NOTICE
    
) VALUES (
    #{hbtBoardNum}
    , #{hbtBoardTitle}
    , #{hbtBoardContent}
    , #{memberVO.memCode}
    
    , #{isPrivate}
    , #{privatePw}
    
    
    , #{isNotice}
    
)

mapper.select:
SELECT FREQ_REQUEST_CODE , FREQ_REQUEST_TITLE , FREQ_REQUEST_CONTENT , REQ.TYPE_REQUEST_CODE , TYPE_REQUEST_NAME FROM HBT_FREQ_REQUEST REQ, TYPE_REQUEST TYP WHERE REQ.TYPE_REQUEST_CODE = TYP.TYPE_REQUEST_CODE
AND REQ.TYPE_REQUEST_CODE = ${typeRequestCode}
ORDER BY TYPE_REQUEST_CODE, FREQ_REQUEST_CODE DESC

SELECT PRIVATE_PW
FROM HBT_BOARD
WHERE HBT_BOARD_NUM = #{#hbtBoardNum}

mapper.update:
UPDATE HBT_BOARD
SET HBT_BOARD_TITLE = #{hbtBoardTitle}, HBT_BOARD_CONTENT = #{hbtBoardContent}
WHERE HBT_BOARD_NUM = #{hbtBoardNum}

mapper.insert:
INSERT INTO HBT_BOARD_REPLY (
    HBT_BOARD_REPLY_NUM
    , HBT_BOARD_REPLY_CONTENT
    , MEM_CODE
    , HBT_BOARD_NUM
	
	, REPLY_DEPTH
	, HBT_BOARD_REPLY_NUM_FK
	
) VALUES(
    #{hbtBoardReplyNum}
    , #{hbtBoardReplyContent}
    , #{memberVO.memCode}
    , #{hbtBoardNum}
    
    , #{replyDepth}
    , #{hbtBoardReplyNumFk}
    
)

INSERT INTO HBT_BOARD_REQUEST (
    HBT_BOARD_REQUEST_NUM
    , HBT_BOARD_REQUEST_TITLE
    , HBT_BOARD_REQUEST_CONTENT
    , MEM_CODE
    , TYPE_REQUEST_CODE
    , REQUEST_PW
    
    , ITEM_CODE
    
)
VALUES (
    #{hbtBoardRequestNum}
    , #{hbtBoardRequestTitle}
    , #{hbtBoardRequestContent}
    , #{memberVO.memCode}
    , ${typeRequestVO.typeRequestCode}
    , #{requestPw}
    
    , #{itemVO.itemCode}
    
)

mapper.select:
SELECT HBT_BOARD_REQUEST_NUM , HBT_BOARD_REQUEST_TITLE , HBT_BOARD_REQUEST_CONTENT , MEM_CODE , TO_CHAR(HBT_BOARD_REQUEST_REG_DATE, 'YYYY-MM-DD HH24:MI') HBT_BOARD_REQUEST_REG_DATE , REQUEST_PW , ITEM_CODE , IS_ANSWER , BOARD.TYPE_REQUEST_CODE , TYPE_REQUEST_NAME FROM HBT_BOARD_REQUEST BOARD, TYPE_REQUEST REQ WHERE BOARD.TYPE_REQUEST_CODE = REQ.TYPE_REQUEST_CODE
AND IS_ANSWER = #{isAnswer}
AND MEM_CODE = #{memberVO.memCode}
ORDER BY HBT_BOARD_REQUEST_NUM
