mapper.insert:
INSERT INTO HBT_BUY (
                     BUY_CODE, MEM_CODE, BUY_STATUS_CODE, BUY_TOTAL_PRICE
) VALUES (
          #{buyCode}
          , (SELECT MEM_CODE FROM HBT_MEM WHERE MEM_ID = #{memCode})
          , 1
          , ${buyTotalPrice}
		)

INSERT INTO HBT_BUY_DETAIL (
            BUY_D_CODE
			, ITEM_CODE
			, BUY_CODE
			, AREA_CODE
			, DEPART_DATE
			, ARRIVE_DATE
			, RESERVED_PEOPLE_NUM
			, BUY_D_PRICE
) VALUES (
          (SELECT 'BUY_D_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BUY_D_CODE, 7))), 0) + 1, 3, '0')
		   FROM HBT_BUY_DETAIL)
		, #{itemCode}
		, #{buyCode}
		, #{areaCode}
		, #{departDate}
		, #{arriveDate}
		, #{reservedPeopleNum}
		, #{buyDPrice}
		 )
