mapper.select:
SELECT COUNT(MEM_ID)
FROM HBT_MEM
WHERE MEM_ID = #{memId}

SELECT COUNT(MEM_EMAIL)
FROM HBT_MEM_DETAIL
WHERE MEM_EMAIL = #{memEmail}

mapper.insert:
	INSERT INTO HBT_MEM (
		MEM_CODE
		, MEM_ID
		, MEM_NAME
		, MEM_PW
	) VALUES(
		(SELECT 'MEM_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MEM_CODE, 5))), 0) + 1, 3, '0') 
    	FROM HBT_MEM)
		, #{memId}
		, #{memName}
		, #{memPw}
    )

	INSERT INTO HBT_MEM_DETAIL(
		MEM_D_CODE
		, MEM_CODE
		, MEM_D_TELL
		, MEM_D_ADDR
		, MEM_D_ADDR2
		, MEM_D_GEN
		, MEM_D_BIR
		, MEM_EMAIL
	) VALUES(
		(SELECT 'MEM_D_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MEM_D_CODE, 7))), 0) + 1, 3, '0') FROM HBT_MEM_DETAIL)
    	, (SELECT 'MEM_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MEM_CODE, 5))), 0) + 1, 3, '0') FROM HBT_MEM_DETAIL)
		, #{memDTell}
		, #{memDAddr} 
		, #{memDAddr2}
		, #{memDGen}
		, #{memDBir}
		, #{memEmail}
    )

mapper.select:
SELECT MEM_ID
FROM HBT_MEM
WHERE MEM_NAME = #{memName}
AND MEM_CODE IN (
    SELECT MEM_CODE
    FROM HBT_MEM_DETAIL
    WHERE MEM_D_TELL = #{memberDetailVO.memDTell}
 	  )

SELECT MEM_EMAIL
FROM HBT_MEM_DETAIL
WHERE MEM_CODE IN (
  SELECT MEM_CODE
  FROM HBT_MEM
  WHERE MEM_ID = #{memId}
    AND MEM_NAME = #{memName}
)
AND MEM_D_TELL = #{memberDetailVO.memDTell}

mapper.update:
UPDATE HBT_MEM
SET
MEM_PW = #{memPw}
WHERE MEM_ID = #{memId}
